const CONSTANTS = {
	MODES: {
		SOLO: 0,
		DUO: 1,
	}
}

var app = {
	category: "",
	track: "",
	mode: CONSTANTS.MODES.SOLO,
	init: function() {
		// window.addEventListener("hashchange", app.handleHashChange, false);
		// media.clear()
		setTimeout(() => {
			document.querySelector('#landing').classList.remove("active")
			app.showOnboarding()
		}, 4000)
	},
	handleHashChange: function() {
		app.goToCategory(cat)
		nav.selectCategory(cat)
	},
	setMode: function(mode) {
		app.mode = mode
		document.body.setAttribute('data-duo-mode', mode)
		document.querySelector('.arcs').classList.add('duo')
	},
	showOnboarding: function() {
		document.querySelector('#onboarding').classList.add("active")
		app.playTrack(179)
	},
	showApp: function() {
		document.querySelector('#onboarding').classList.remove("active")
		document.querySelector('#app').classList.add("active")
	},
	playTrack: function(id) {
		if(app.track != id) {
			app.track = id

			if(tracklist.activeTrack) {
				tracklist.activeTrack.classList.remove('active')
				tracklist.activeTrack = document.querySelector(`#tracklist .track[data-id="${id}"]`)
				tracklist.activeTrack.classList.add('active')
			}

			let trackObj = data.tracks[id]
			if(!trackObj) 
				console.log("error playing track")
			else
				media.setTrack(trackObj)
		}
		media.togglePlay()
	},
	goToCategory: function(category) {
		if(app.category == category) return

		app.category = category;
		document.body.setAttribute("category", category);
		tracklist.render(category)
	},
	goToHome: function() {
		nav.open()
	},
	hideContent: function() {
		document.getElementById('tracklist').classList.add("hide")
	},
	showContent: function() {
		document.getElementById('tracklist').classList.remove("hide")
	},
}

document.addEventListener("DOMContentLoaded", app.init);
var nav = {
	dom: null,
	activeCatDOM: null,
	timeoutId: "",
	init: function() {
		nav.dom = document.getElementById("nav");
		// getComputedStyle(document.body).getPropertyValue('--color-font-general'));
	},
	handleClick: function(category, catDom) {
		if(nav.dom.classList.contains("close")) {
			nav.open()
		} else {
			app.goToCategory(category)
			nav.selectCategory(category, catDom)
		}
	},
	selectCategory: function(category, optDom) {
		if(nav.activeCatDOM) {
			nav.activeCatDOM.classList.remove("active")
		}
		nav.activeCatDOM = optDom ? optDom : nav.dom.querySelector("[data-category='" + category + "']")
		nav.activeCatDOM.classList.add("active")
		nav.close()
	},
	close: function() {
		nav.dom.classList.add("close")
		app.showContent()

		setTimeout(() => {
			nav.dom.querySelectorAll(".category:not(.active)").forEach((catDom) => {
				catDom.classList.add("hidden")
			})
		}, 500)
	},
	open: function() {
		if(nav.timeoutId) clearTimeout(nav.timeoutId)

		nav.dom.querySelectorAll(".category:not(.active)").forEach((catDom) => {
			catDom.classList.remove("hidden")
		})
		nav.dom.classList.remove("close")
		app.hideContent()
	}
}

document.addEventListener("DOMContentLoaded", nav.init);
var tracklist = {
	dom: null,
	template: null,
	activeTrack: null,
	init: function() {
		tracklist.dom = document.getElementById("tracklist")
		tracklist.template = document.getElementById("template-tracklist-item").content
	},
	handleClick: function(dom) {
		let id = dom.getAttribute("data-id")
		// if(tracklist.activeTrack && tracklist.activeTrack.getAttribute("data-id") == id) return

		if(tracklist.activeTrack) tracklist.activeTrack.classList.remove('active')
		dom.classList.add('active')
		tracklist.activeTrack = dom
		app.playTrack(id)
	},
	render: function(category) {
		var fragment = document.createDocumentFragment();
		var number = 0;
		console.log("dat is ///" + data)
		data.categories[category].forEach((id, index) => {
			let trackObj = data.tracks[id]

			if(app.mode != parseInt(trackObj.type)) {
				console.log(trackObj)
				return
			}
			let templ = document.importNode(tracklist.template, true)
			templ.querySelector(".track").setAttribute("data-id", id)
			templ.querySelector(".id").innerHTML = ++number
			templ.querySelector(".title").innerHTML = trackObj.title
			templ.querySelector(".description").innerHTML = trackObj.description
      if(trackObj.length) {
        let date = new Date(null)
			  date.setSeconds(trackObj.length)
			  templ.querySelector(".length").innerHTML = date.toISOString().substr(14, 5)
      }

      console.log("adding...")
      if(trackObj.url == "") {
      	templ.querySelector(".track").classList.add('disable')
      	templ.querySelector(".track").onclick = ""
      }
			fragment.appendChild(templ)
		})

		tracklist.dom.querySelector('.category').innerHTML = category
		tracklist.dom.querySelector('.tracks').innerHTML = ""
		tracklist.dom.querySelector('.tracks').appendChild(fragment)
	},
}

document.addEventListener("DOMContentLoaded", tracklist.init);

var carousel = {
	dom: null,
	init: function() {
		carousel.dom = document.getElementById('carousel')
		console.log('initing')
		var observer = new MutationObserver(function(mutationsList, observer) {
    	for(var mutation of mutationsList) {
				if(mutation.type == 'attributes') {
					carousel._goToIndex(parseInt(carousel.dom.getAttribute(mutation.attributeName)))
				}
			}
		});
		observer.observe(carousel.dom, { attributes: true });
		carousel._goToIndex(0)
	},
	onDotClicked: function(index) {
		carousel.dom.setAttribute('data-index', index)
	},
	_goToIndex: function(index) {
		let carouselSize = parseInt(carousel.dom.getAttribute('data-num-items'))
		if(index == carouselSize) return

		let container = carousel.dom.querySelector(".container")
		container.style.transform = `translateX(${index * -100 / carouselSize}%)`

		carousel.dom.querySelectorAll('.dot').forEach((dot, i) => {
			dot.classList.remove('active')
			if(i == index) dot.classList.add('active')
		})
	},
}

document.addEventListener("DOMContentLoaded", carousel.init);
var media = {
	dom: null,
	player: null,
	playPromise: null,
	isPlaying: false,
	timeInterval: null,
	init: function() {
		media.dom = document.getElementById("media")
		media.player = document.getElementById('media-player')
		media.player.addEventListener('ended', media.handleTrackFinished);
	},
	clear: function() {
		media.dom.classList.remove("active", "ended")
		media.isPlaying = false
		media.player.pause()
		media.player.currentTime = 0
		media.dom.querySelector("source").src = ""
	},
	toggle: function() {
		media.dom.classList.toggle('expand')
	},
	startTime: function() {
		let timeDom = media.dom.querySelector(".currentTime")
		let progressDom = media.dom.querySelector('.progress .bar')
		media.timeInterval = setInterval(() => {
			progressDom.style.width = `${media.player.currentTime / media.player.duration * 100}%`
			let min = Math.floor(media.player.currentTime / 60).toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false})
			let sec = Math.floor(media.player.currentTime - min).toLocaleString('en-US', {minimumIntegerDigits: 2, useGrouping:false})
			timeDom.innerHTML = `${min}:${sec}`
		}, 1000)
	},
	stopTime: function() {
		clearInterval(media.timeInterval)
	},
	togglePlay: function() {
  	media.dom.classList.toggle("active")

  	if(media.player.currentTime > 0 && 
  		!media.player.paused && 
  		!media.player.ended) {
  		media.player.pause()
  		media.stopTime()
  	} else {
	  	media.playPromise = media.player.play()
		  if (media.playPromise !== undefined) {
		    media.playPromise.then(_ => {
  				media.startTime()
		      media.dom.classList.add("play")
		    })
		    .catch(error => {
		    	console.log("Error with audio")
		    });
		  }
  	}
	},
	setTrack: function(trackObj) {
		media.dom.classList.remove("active", "ended")
		document.querySelector('.arcs').classList.remove('animate')

		if(trackObj.length) {
			let date = new Date(null)
			date.setSeconds(trackObj.length)
			media.dom.querySelector(".duration").innerHTML = date.toISOString().substr(14, 5)
		}
		media.dom.querySelector(".title").innerHTML = trackObj.title
		media.dom.querySelector(".currentTime").innerHTML = "00:00"
		media.dom.querySelector("source").src = trackObj.url
		media.dom.querySelector("audio").src = trackObj.url
	},
	handleTrackFinished(e) {
		media.dom.classList.remove("active")
		media.dom.classList.add("ended")
		document.querySelector('.arcs').classList.add('animate')
	},
}

document.addEventListener("DOMContentLoaded", media.init)
